import React, { useEffect, useState } from "react";
import Header from "./Header";
import NotFound from "./NotFound";
import "../index.js";
import {
  BrowserRouter as Router,
  Link,
  NavLink,
  Route,
  Switch,
  useLocation,
  useHistory,
  useParams,
  useRouteMatch,
} from "react-router-dom";

function Study() {
  const { deckId } = useParams();
  const history = useHistory();
  const [deckName, setDeckName] = useState('');
  const [cardsList, setCardsList] = useState([]);
  const [currentCard, setCurrentCard] = useState(0);
  const [isFlipped, setIsFlipped] = useState(false);
  const [showNext, setShowNext] = useState(false);
  const [cardContent, setCardContent] = useState('');

  useEffect(() => {
    fetch(`http://localhost:5000/decks/${deckId}?_embed=cards`)
      .then(res => res.json())
      .then(json => {
        setDeckName(json.name);
        setCardsList(json.cards);
        setCardContent(json.cards[currentCard].front);
      })
  }, []);

  useEffect(() => {
    if (cardsList.length > 0) {
      setCardContent(cardsList[currentCard].front);
    }
  }, [currentCard])

  const flipCard = () => {
    if (isFlipped) {
      setCardContent(cardsList[currentCard].front);
    } else {
      setCardContent(cardsList[currentCard].back);
    }
    setIsFlipped(!isFlipped);
    setShowNext(true);
  }

  const nextCard = () => {
    if (currentCard+1 === cardsList.length) {
      if (window.confirm('Restart Cards?\n\nClick "cancel" to return to the home page.')) {
        setIsFlipped(false);
        setShowNext(false);
        setCurrentCard(0);
      } else {
        history.push('/');
      }
    } else {
      setIsFlipped(false);
      setShowNext(false);
      setCurrentCard(currentCard+1);
    }
  }
  
  return (
    <div>
      <nav aria-label="breadcrumb">
        <ol class="breadcrumb">
          <li class="breadcrumb-item">
            <Link to="/">Home</Link>
          </li>
          <li class="breadcrumb-item">
            <Link to={`/decks/${deckId}`}>{deckName}</Link>
          </li>
          <li class="breadcrumb-item active" aria-current="page">
            Study
          </li>
        </ol>
      </nav>
      <div>
        <h2 class="card-title">Study: {deckName}</h2>
      </div>
      <div class="card">
        <div class="card-body">
        {
          cardsList.length > 2 
          ? <div class="flip-card">
              <div class="flip-card-inner">
                <div class="flip-card-front">
                  <h5 class="card-title">Card {currentCard + 1} out of {cardsList.length}</h5>
                  <p class="card-text">
                    {cardContent}
                  </p>
                  <div class="d-grid gap-2 d-md-block"></div>
                </div>
                <button onClick={flipCard} class="btn btn-secondary" type="button">Flip</button>
                { showNext 
                  ? <button onClick={nextCard} class="btn btn-primary" type="button">Next</button>
                  : null
                }
              </div>
            </div>
          : <div>
              <h4>Not enough cards.</h4>
              <p>You need at least 3 cards to study. There are {cardsList.length} cards in this deck.</p>
              <button class="btn btn-primary">
                <span className="oi oi-plus"></span> Add Cards
              </button>
            </div>
        }
        </div>
      </div>
    </div>
  );
}

export default Study;
