import React, { useEffect, useState } from "react";
import Header from "./Header";
import NotFound from "./NotFound";
import Home from "./Home";
import Study from "./Study";
import CardList from "./CardList"
import {
  stripCards,
  listDecks,
  createDeck,
  readDeck,
  updateDeck,
  deleteDeck,
  listCards,
  createCard,
  readCard,
  updateCard,
  deleteCard,
} from "../utils/api/index";
import {
  BrowserRouter as Router,
  Link,
  NavLink,
  Route,
  Switch,
  useLocation,
  useHistory,
  useParams,
  useRouteMatch,
} from "react-router-dom";

function DeckOverview(props) {
  const { deckId } = useParams();
  const [deckData, setDeckData] = useState()

  //code for showing deckName
     //have to initialize deckName so it does not render as undefined
    //deck id 1 is deckList[0]
    // let deckName = null;
    // if (props.deckList.length) {
    //   deckName = props.deckList[deckId-1].name;
    // } else {
    //   deckName = null;
    // }
  
    // let deckDescription = null;
    // if (props.deckList.length) {
    //   deckDescription = props.deckList[deckId-1].description;
    // } else {
    //   deckDescription = null;
    // }

  //code for getting cards in the deck to display
  const [cardListState, setCardListState] = useState([])
  
  useEffect(() => {
    async function loadCards() {
      let listOfCards = await listCards(deckId)
      setCardListState(listOfCards)
    }
    loadCards()
  },[])
  console.log(cardListState, 'cardliststate')

  const mappedCards = cardListState.map((card) => {
    return (
      <CardList
        key={card.id}
        front={card.front}
        back={card.back}
        id={card.id}        
      />
    );
  });



  // console.log(deckId)
  // console.log(props.id, 'props.id is undefined')
  // console.log(props.deckList, 'props.deckList')

  // if(Object.keys(props.deckList).length){
  //   console.log(props.deckList[0].id, 'props.deckList[0]')
  //}












  return (
    <div>
      <nav aria-label="breadcrumb">
        <ol className="breadcrumb">
          <li className="breadcrumb-item">
            <Link to="/">Home</Link>
          </li>
          <li className="breadcrumb-item active" aria-current="page">
             {deckName && deckName}
          </li>
        </ol>
      </nav>
      <h4> {deckName && deckName}</h4>
      <p>
        {deckDescription && deckDescription}
      </p>

      <div>
        <Link to={`/decks/${deckId}/edit`} class="btn btn-secondary">
          {``} {``} Edit
        </Link>
        <Link to={`/decks/${deckId}/study`} class="btn btn-primary">
          {``} {``} Study
        </Link>
        <Link to={`/decks/${deckId}/cards/new`} class="btn btn-primary">        
          <span className="oi oi-plus" />
          {``} {``} Add Cards
        </Link>
        <Link to="/decks/delete" class="btn btn-danger">
          <span className="oi oi-trash" />
        </Link>

        <h2 class="card-title">Cards</h2>
        <br />
        <div>
         {mappedCards}
        </div>
      </div>
     
    </div>
  );
}

export default DeckOverview;
