import React, { useEffect, useState } from "react";
import Header from "./Header";
import NotFound from "./NotFound";
import {
  stripCards,
  listDecks,
  createDeck,
  readDeck,
  updateDeck,
  deleteDeck,
  listCards,
  createCard,
  readCard,
  updateCard,
  deleteCard,
} from "../utils/api/index";
import {
  BrowserRouter as Router,
  Link,
  NavLink,
  Route,
  Switch,
  useLocation,
  useHistory,
  useParams,
  useRouteMatch,
} from "react-router-dom";

function DeckList(props) {
  const handleDeleteClick = async () => {
    if (
      window.confirm("Delete this deck?\n\nYou will not be able to recover it.")
    ) {
      await deleteDeck(props.id);
      props.handleDeleteDeck(props.id);
    }
  };
  

  return (
    <div className="card">
      <div className="card-body">
        <h5 className="card-title">{props.name}</h5>
        <h5 className="card-title"># of cards: {props.cards.length}</h5>
        <p className="card-text">{props.description}</p>
        <Link to={`/decks/${props.id}`} className="btn btn-secondary">
          View
        </Link>
        <Link to={`/decks/${props.id}/study`} className="btn btn-primary">
          Study
        </Link>
        <Link
          to="/decks/delete"
          className="btn btn-danger"
          onClick={handleDeleteClick}
        >
          <span className="oi oi-trash" />
        </Link>
      </div>
    </div>
  );
}

export default DeckList;
