import React, { useEffect, useState } from "react";
import Header from "./Header";
import NotFound from "./NotFound";
import Home from "./Home";
import Study from "./Study";
import NewDeck from "./NewDeck";
import DeckOverview from "./DeckOverview";
import EditDeck from "./EditDeck";
import DeckList from "./DeckList";
import AddCard from "./AddCard"
import EditCard from "./EditCard";


import {
  stripCards,
  listDecks,
  createDeck,
  readDeck,
  updateDeck,
  deleteDeck,
  listCards,
  createCard,
  readCard,
  updateCard,
  deleteCard,
} from "../utils/api/index";
import {
  BrowserRouter as Router,
  Link,
  NavLink,
  Route,
  Switch,
  useLocation,
  useHistory,
  useParams,
  useRouteMatch,
} from "react-router-dom";

function Layout() {
  const [deckList, setDeckList] = useState([]);

  const handleDeleteDeck = (deckId) => {
    setDeckList(deckList.filter(deck => deck.id !== deckId));
  }

  const handleCreateDeck = (deck) => {
    setDeckList([...deckList, deck]);
  }



  useEffect(() => {
    listDecks().then(setDeckList)
    // async function loadDeck() {
    //   let allDecks = await listDecks();
    //   setDeckList(allDecks);
    // }
    // loadDeck();
  }, []);

  const mappedDecks = deckList.map((deck) => {
    console.log(deck, 'this is deck')
    return (
      <DeckList
        key={deck.id}
        deck={deck} 
        // description={deck.description}
        // cards={deck.cards}
        // id={deck.id}
        // name={deck.name}
        handleDeleteDeck={handleDeleteDeck}
      />
    );
  });

  return (
    <div>
      <Header />
      <div className="container">
        <Switch>
          <Route path="/" exact={true}>
            <Home mappedDecks={mappedDecks} handleDeleteDeck={handleDeleteDeck}/>
          </Route>
          <Route path="/decks/new" exact={true}>
            <NewDeck handleCreateDeck={handleCreateDeck}/>
          </Route>
          <Route path="/decks/:deckId" exact={true}>
            <DeckOverview deckList={deckList}/>
          </Route>

          <Route path="/decks/:deckId/study">
            <Study />
          </Route>
          <Route path="/decks/:deckId/edit">
            <EditDeck deckList={deckList}/>
          </Route>
          <Route path="/decks/:deckId/cards/new">
            <AddCard deckList={deckList}/>
          </Route>
          <Route path="/decks/:deckId/cards/:cardId/edit" exact={true}>
            <EditCard />
          </Route>
          <Route>
            <NotFound />
          </Route>
        </Switch>
      </div>
    </div>
  );
}

export default Layout;
